#include "ftshell.h"

int	last_slash(const char *in)
{
	int		ret;
	int		i;

	ret = -1;
	i = 0;
	while (*in)
	{
		if (*in == '/')
			ret = i;
		in++;
		i++;
	}
	return (ret);
}
